import React from 'react';
import { Link } from 'react-router-dom';

export default class CreateAccount extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            username : "",
            email : "",
            password : "",
        };

        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleChange = this.handleChange.bind(this)
    }

    handleFileInput(e){
        console.log(e) 
        // this.setState({[e.target.name]: e.target.value});
        var imagedata = document.querySelector('input[type="file"]').files[0];
        this.setState({[e.target.name]: imagedata});
        console.log(this.state)
    }

    handleChange(event) {
        this.setState({[event.target.name]: event.target.value});
        console.log(this.state)
    }

    handleSubmit(e){
        e.preventDefault()

        let form_data = new FormData();
        form_data.append('image', this.state.image.name);//, this.state.image.name);
        form_data.append('title', this.state.title);
        form_data.append('content', this.state.content);
        // form_data.append('method', 'POST')
        console.log(form_data)
        let url = 'http://localhost:8000/post/';
        axios.post(url, form_data, {
            headers: {
            'content-type': 'multipart/form-data',
            'accept': 'application/json',
            }
        })
            .then(res => {
                console.log(res.data);
            })
            .catch(err => console.log("ERROR :", err))

        // fetch("http://127.0.0.1:8000/create-user/", {
