import React from 'react';
import { Link } from 'react-router-dom';
import Modal from './Modal';

export default class Login extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            longindetails: {username: "", password: ""},
            show:false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    showModal = () => {
        this.setState({
            ...this.state.show,
            show: true
        })
    }

    hideModal = () => {
        this.setState({
            ...this.state.show,
            show: false
        })
    }

    handleChange(event) {
        this.setState({[event.target.name]: event.target.value});
        console.log(this.state)
    }

    handleSubmit(event) {
        // alert('A username and password was submitted: ' + this.state.username + " " + this.state.password);
        event.preventDefault();
        fetch('http://127.0.0.1:8000/token/obtain/', {
            method : 'POST',
            mode : 'cors',
            cache : 'no-cache',
            credentials : 'omit',
            headers: {
                // 'Content-Type' : 'application/x-www-form-urlencoded'
                'Content-Type': 'application/json',
                'accept': 'application/json',               
            }, 
            body: JSON.stringify(this.state)
        })
        .then(response => response.json())
        .then(data => {
            console.log('Data :', data)
            localStorage.setItem('access_token', data.access)
            localStorage.setItem('refresh_token', data.refresh)
            if (data.access){
                console.log("ffgjkhj")
            }
            else if(data.detail){
                // 'No active account found with the given credentials'
                // console.log('not valid')
            } 
        })
        .catch((error) => {
            console.log(error)
        })
    }

    render() {
        return(
            <form onSubmit={this.handleSubmit}>
                <h3>Sign In</h3>

                <div className="form-group">
                    <label>Username</label>
                    <input type="text" name="username" value={this.state.username} onChange={this.handleChange} className="form-control" placeholder="Enter username" />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" name="password" value={this.state.password} onChange={this.handleChange} className="form-control" placeholder="Enter password" />
                </div>

                <div className="form-group">
                    <div className="custom-control custom-checkbox">
                        <input type="checkbox" className="custom-control-input" id="customCheck1" />
                        <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                    </div>
                </div>

                <button type="submit" className="btn btn-primary btn-block">Submit</button>
                <p className="forgot-password text-right">
                    Forgot <a href="#">password?</a>
                </p>
                <p className="create-account text-right">
                    Click to <Link to={"/sign-up"}>Create an Account</Link>
                </p>
            </form>
        )
    }
}
