import React from 'react';
import Modal from './Modal';
import { Link } from 'react-router-dom';

export default class Profile extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            show:false,
            currentuser: [],
            longindetails: {}
        }

        this.getCurentUser = this.getCurentUser.bind(this)
    }

    componentDidMount() {
        this.getCurentUser()
    }

    showModal = () => {
        this.setState({
            ...this.state.show,
            show: true
        })
    }

    hideModal = () => {
        this.setState({
            ...this.state.show,
            show: false
        })
    }

    getCurentUser(){
        fetch('http://127.0.0.1:8000/api/users/',{ 
            method : 'GET',
            mode : 'cors',
            cache : 'no-cache',
            credentials : 'omit',
            headers: {
                // 'Content-Type' : 'application/x-www-form-urlencoded'
                'Content-Type': 'application/json',
                'accept': 'application/json',  
                'Authorization': 'JWT ' + localStorage.getItem('access_token')             
            }, 
            // body: JSON.stringify(this.state)
        })
        .then(response => response.json())
        .then(data => {
            console.log('Data :', data)
            if(data.code === "token_not_valid"){
                this.showModal()
            }
        })
        .catch((error) => {
            console.log(error)
        })
    }
    render(){
        return(
            <div>
                <h2 style={{textAlign:'center'}}>User Profile</h2>
                <div className="card">

                    <img src="" alt="img" style={{width:'100%'}}/>
                    <h1>name</h1>
                    <p className="title">Student</p>
                    <p>School</p>
                    <div>

                    </div>
                </div>
                <Modal show={this.state.show} handleClose={this.hideModal}>
                    <p>Modal</p>
                    Session closed

                    <form onSubmit={this.handleSubmit}>
                        <h3>Sign In</h3>

                        <div className="form-group">
                            <label>Username</label>
                            <input type="text" name="username" value={this.state.longindetails.username} onChange={this.handleChange} className="form-control" placeholder="Enter username" />
                        </div>

                        <div className="form-group">
                            <label>Password</label>
                            <input type="password" name="password" value={this.state.longindetails.password} onChange={this.handleChange} className="form-control" placeholder="Enter password" />
                        </div>

                        <div className="form-group">
                            <div className="custom-control custom-checkbox">
                                <input type="checkbox" className="custom-control-input" id="customCheck1" />
                                {/* <label className="custom-control-label" htmlFor="customCheck1">Remember me</label> */}
                            </div>
                        </div>

                        <button type="submit" className="btn btn-primary btn-block">Submit</button>
                        <p className="forgot-password text-right">
                            Forgot <a href="#">password?</a>
                        </p>
                        <p className="create-account text-right">
                            Click to <Link to={"/sign-up"}>Create an Account</Link>
                        </p>
                    </form>
                    <br />
                    {/* <Link to={"/sign-in"}>Login</Link> */}
                </Modal>
            </div>
        )
    }
}
