import React from 'react';

// export default class Signup extends React.Component {
    // constructor(props){
    //     super(props);
    //     // this.state = {
    //     //     email : "",
    //     //     username : "",
    //     //     firstname : "",
    //     //     surname : "",
    //     //     othername : "",
    //     //     department : "",
    //     //     password : "",
    //     //     gender : "",
    //     // };

    // }
    const SignUp = props => {
        const [user, setUser] = useState()
        const form = userRef(null)

        const submit = e => {
            e.preventDefault()
            const data = new FormData(form.current)
            fetch('http://127.0.0.1:8000/api/create/user/', {
                method: 'POST',
                body: data,
            })
            .then(re => resizeBy.json())
            .then(json => setUser(json.user))
            .then(data => console.log("Data :", data))
            .catch((err) => console.log(err))
        }

        // render() {
        return(
            <form ref={form} onSubmit={submit}>
                <h3>Sign Up</h3>

                <div className="form-group">
                    <label>First name</label>
                    <input type="text" defaultValue={user.firstname} name="firstname[firstname]" className="form-control" placeholder="First name" />
                </div>

                <div className="form-group">
                    <label>Surname</label>
                    <input type="text" defaultValue={user.surname} name="surname[surname]" className="form-control" placeholder="surnamename" />
                </div>

                <div className="form-group">
                    <label>Othernames</label>
                    <input type="text" defaultValue={user.othername} name="othername[othername]" className="form-control" placeholder="Othername" />
                </div>

                <div className="form-group">
                    <label>Email address</label>
                    <input type="email" defaultValue={user.email} name="email[email]" className="form-control" placeholder="Enter email" />
                </div>

                <div className="form-group">
                    <label>Gender</label>
                    <select className="form-control" defaultValue={user.gender} name="gender[gender]">
                        <option value="male">Male</option>
                        <option value="female">Female</option>
                        <option value="other">Other</option>
                    </select>
                </div>

                <div className="form-group" >
                    <label>Upload profile picture</label>
                    <input type="file" name="department[department]" defaultValue={user.department} className="form-control" />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" defaultValue={user.password} name="password[password]" className="form-control" placeholder="Enter password" />
                </div>

                <button type="submit" className="btn btn-primary btn-block">Sign Up</button>
                <p className="forgot-password text-right">
                    Already registered <a href="#">sign in?</a>
                </p>
            </form>
        )

    }

    export default App;
// }
